mapper.update:
	update tb_qna
 	   set view_cnt = view_cnt+1
 	 where no=#{no}

update tb_qna
   set title = #{title}, content = #{content}
 where no = #{no}

mapper.select:
select count(*)
  from tb_qna_comment
 where no=#{no}

mapper.update:
update tb_qna_comment
   set content = #{content}
 where comment_no = #{commentNo}

update tb_qna
   set qna_status = 'y'
 where no = #{no}

update tb_qna_comment
   set qna_status = 'y'
 where comment_no = #{commentNo}

mapper.select:
select count(*)
 from tb_qna_recom
 where no=#{no}
   and id=#{id}

select count(*)
  from tb_qna_recom
 where no = #{no}

mapper.delete:
delete from tb_qna_recom
 where no=#{no}
   and id=#{id}
